use chrono::{DateTime, Utc};
use influxdb::Error;
use influxdb::InfluxDbWriteable;
use influxdb::{Client, ReadQuery};
use std::sync::Arc;
use std::time::Instant;
use tokio::sync::mpsc::unbounded_channel;
use tokio::sync::Semaphore;

#[derive(InfluxDbWriteable, Clone)]
struct WeatherReading {
    time: DateTime<Utc>,
    humidity: i32,
    #[influxdb(tag)]
    wind_direction: String,
}

#[tokio::main]
async fn main() {
    let db_name = "bench";
    let url = "http://localhost:8086";
    let number_of_total_requests = 20000;
    let concurrent_requests = 1000;

    let client = Client::new(url, db_name);
    let concurrency_limit = Arc::new(Semaphore::new(concurrent_requests));

    prepare_influxdb(&client, db_name).await;
    let measurements = generate_measurements(number_of_total_requests);
    let (tx, mut rx) = unbounded_channel::<Result<String, Error>>();

    let start = Instant::now();
    for m in measurements {
        let permit = concurrency_limit.clone().acquire_owned().await;
        let client_task = client.clone();
        let tx_task = tx.clone();
        tokio::spawn(async move {
            let res = client_task.query(&m.into_query("weather")).await;
            let _ = tx_task.send(res);
            drop(permit);
        });
    }
    drop(tx);

    let mut successful_count = 0;
    let mut error_count = 0;
    while let Some(res) = rx.recv().await {
        if res.is_err() {
            error_count += 1;
        } else {
            successful_count += 1;
        }
    }

    let end = Instant::now();

    println!(
        "Throughput: {:.1} request/s",
        1000000.0 * successful_count as f64 / (end - start).as_micros() as f64
    );
    println!(
        "{} successful requests, {} errors",
        successful_count, error_count
    );
}

async fn prepare_influxdb(client: &Client, db_name: &str) {
    let create_db_stmt = format!("CREATE DATABASE {}", db_name);
    client
        .query(&ReadQuery::new(create_db_stmt))
        .await
        .expect("failed to create database");
}

fn generate_measurements(n: u64) -> Vec<WeatherReading> {
    (0..n)
        .collect::<Vec<u64>>()
        .iter_mut()
        .map(|_| WeatherReading {
            time: Utc::now(),
            humidity: 30,
            wind_direction: String::from("north"),
        })
        .collect()
}

//! Client which can read and write data from InfluxDB.
//!
//! # Arguments
//!
//!  * `url`: The URL where InfluxDB is running (ex. `http://localhost:8086`).
//!  * `database`: The Database against which queries and writes will be run.
//!
//! # Examples
//!
//! ```rust
//! use influxdb::Client;
//!
//! let client = Client::new("http://localhost:8086", "test");
//!
//! assert_eq!(client.database_name(), "test");
//! ```

use futures_util::TryFutureExt;
use reqwest::{Client as HttpClient, RequestBuilder, Response as HttpResponse};
use std::collections::{BTreeMap, HashMap};
use std::fmt::{self, Debug, Formatter};
use std::sync::Arc;

use crate::query::QueryType;
use crate::Error;
use crate::Query;

#[derive(Clone)]
/// Internal Representation of a Client
pub struct Client {
    pub(crate) url: Arc<String>,
    pub(crate) parameters: Arc<HashMap<&'static str, String>>,
    pub(crate) token: Option<String>,
    pub(crate) client: HttpClient,
}

struct RedactPassword<'a>(&'a HashMap<&'static str, String>);

impl<'a> Debug for RedactPassword<'a> {
    fn fmt(&self, f: &mut Formatter<'_>) -> fmt::Result {
        let entries = self
            .0
            .iter()
            .map(|(k, v)| match *k {
                "p" => (*k, "<redacted>"),
                _ => (*k, v.as_str()),
            })
            .collect::<BTreeMap<&'static str, &str>>();
        f.debug_map().entries(entries).finish()
    }
}

impl Debug for Client {
    fn fmt(&self, f: &mut Formatter<'_>) -> fmt::Result {
        f.debug_struct("Client")
            .field("url", &self.url)
            .field("parameters", &RedactPassword(&self.parameters))
            .finish_non_exhaustive()
    }
}

impl Client {
    /// Instantiates a new [`Client`](crate::Client)
    ///
    /// # Arguments
    ///
    ///  * `url`: The URL where InfluxDB is running (ex. `http://localhost:8086`).
    ///  * `database`: The Database against which queries and writes will be run.
    ///
    /// # Examples
    ///
    /// ```rust
    /// use influxdb::Client;
    ///
    /// let _client = Client::new("http://localhost:8086", "test");
    /// ```
    #[must_use = "Creating a client is pointless unless you use it"]
    pub fn new<S1, S2>(url: S1, database: S2) -> Self
    where
        S1: Into<String>,
        S2: Into<String>,
    {
        let mut parameters = HashMap::<&str, String>::new();
        parameters.insert("db", database.into());
        Client {
            url: Arc::new(url.into()),
            parameters: Arc::new(parameters),
            client: HttpClient::new(),
            token: None,
        }
    }

    /// Add authentication/authorization information to [`Client`](crate::Client)
    ///
    /// # Arguments
    ///
    /// * username: The Username for InfluxDB.
    /// * password: The Password for the user.
    ///
    /// # Examples
    ///
    /// ```rust
    /// use influxdb::Client;
    ///
    /// let _client = Client::new("http://localhost:9086", "test").with_auth("admin", "password");
    /// ```
    #[must_use = "Creating a client is pointless unless you use it"]
    pub fn with_auth<S1, S2>(mut self, username: S1, password: S2) -> Self
    where
        S1: Into<String>,
        S2: Into<String>,
    {
        let mut with_auth = self.parameters.as_ref().clone();
        with_auth.insert("u", username.into());
        with_auth.insert("p", password.into());
        self.parameters = Arc::new(with_auth);
        self
    }

    /// Replaces the HTTP Client
    #[must_use = "Creating a client is pointless unless you use it"]
    pub fn with_http_client(mut self, http_client: HttpClient) -> Self {
        self.client = http_client;
        self
    }

    /// Add authorization token to [`Client`](crate::Client)
    ///
    /// This is designed for influxdb 2.0's backward-compatible API which
    /// requires authorization by default. You can create such token from
    /// console of influxdb 2.0 .
    pub fn with_token<S>(mut self, token: S) -> Self
    where
        S: Into<String>,
    {
        self.token = Some(token.into());
        self
    }

    /// Returns the name of the database the client is using
    pub fn database_name(&self) -> &str {
        // safe to unwrap: we always set the database name in `Self::new`
        self.parameters.get("db").unwrap()
    }

    /// Returns the URL of the InfluxDB installation the client is using
    pub fn database_url(&self) -> &str {
        &self.url
    }

    /// Pings the InfluxDB Server
    ///
    /// Returns a tuple of build type and version number
    pub async fn ping(&self) -> Result<(String, String), Error> {
        let url = &format!("{}/ping", self.url);
        let res = self
            .client
            .get(url)
            .send()
            .await
            .map_err(|err| Error::ProtocolError {
                error: err.to_string(),
            })?;

        const BUILD_HEADER: &str = "X-Influxdb-Build";
        const VERSION_HEADER: &str = "X-Influxdb-Version";

        let (build, version) = {
            let hdrs = res.headers();
            (
                hdrs.get(BUILD_HEADER).and_then(|value| value.to_str().ok()),
                hdrs.get(VERSION_HEADER)
                    .and_then(|value| value.to_str().ok()),
            )
        };

        Ok((build.unwrap().to_owned(), version.unwrap().to_owned()))
    }

    /// Sends a [`ReadQuery`](crate::ReadQuery) or [`WriteQuery`](crate::WriteQuery) to the InfluxDB Server.
    ///
    /// A version capable of parsing the returned string is available under the [serde_integration](crate::integrations::serde_integration)
    ///
    /// # Arguments
    ///
    ///  * `q`: Query of type [`ReadQuery`](crate::ReadQuery) or [`WriteQuery`](crate::WriteQuery)
    ///
    /// # Examples
    ///
    /// ```rust,no_run
    /// use influxdb::{Client, Query, Timestamp};
    /// use influxdb::InfluxDbWriteable;
    /// use std::time::{SystemTime, UNIX_EPOCH};
    ///
    /// # #[tokio::main]
    /// # async fn main() -> Result<(), influxdb::Error> {
    /// let start = SystemTime::now();
    /// let since_the_epoch = start
    ///   .duration_since(UNIX_EPOCH)
    ///   .expect("Time went backwards")
    ///   .as_millis();
    ///
    /// let client = Client::new("http://localhost:8086", "test");
    /// let query = Timestamp::Milliseconds(since_the_epoch)
    ///     .into_query("weather")
    ///     .add_field("temperature", 82);
    /// let results = client.query(query).await?;
    ///
    /// # Ok(())
    /// # }
    /// ```
    /// # Errors
    ///
    /// If the function can not finish the query,
    /// a [`Error`] variant will be returned.
    ///
    /// [`Error`]: enum.Error.html
    pub async fn query<Q>(&self, q: Q) -> Result<String, Error>
    where
        Q: Query,
    {
        let query = q.build().map_err(|err| Error::InvalidQueryError {
            error: err.to_string(),
        })?;

        let mut parameters = self.parameters.as_ref().clone();
        let request_builder = match q.get_type() {
            QueryType::ReadQuery => {
                let read_query = query.get();
                let url = &format!("{}/query", &self.url);
                parameters.insert("q", read_query.clone());

                if read_query.contains("SELECT") || read_query.contains("SHOW") {
                    self.client.get(url).query(&parameters)
                } else {
                    self.client.post(url).query(&parameters)
                }
            }
            QueryType::WriteQuery(precision) => {
                let url = &format!("{}/write", &self.url);
                let mut parameters = self.parameters.as_ref().clone();
                parameters.insert("precision", precision);

                self.client.post(url).body(query.get()).query(&parameters)
            }
        };

        let res = self
            .auth_if_needed(request_builder)
            .send()
            .map_err(|err| Error::ConnectionError {
                error: err.to_string(),
            })
            .await?;
        check_status(&res)?;

        let body = res.text();

        let s = body.await.map_err(|_| Error::DeserializationError {
            error: "response could not be converted to UTF-8".into(),
        })?;

        // todo: improve error parsing without serde
        if s.contains("\"error\"") || s.contains("\"Error\"") {
            return Err(Error::DatabaseError {
                error: format!("influxdb error: {s:?}"),
            });
        }

        Ok(s)
    }

    fn auth_if_needed(&self, rb: RequestBuilder) -> RequestBuilder {
        if let Some(ref token) = self.token {
            rb.header("Authorization", format!("Token {token}"))
        } else {
            rb
        }
    }
}

pub(crate) fn check_status(res: &HttpResponse) -> Result<(), Error> {
    let status = res.status();
    if !status.is_success() {
        return Err(Error::ApiError(status.into()));
    }
    Ok(())
}

#[cfg(test)]
mod tests {
    use super::Client;
    use indoc::indoc;

    #[test]
    fn test_client_debug_redacted_password() {
        let client = Client::new("https://localhost:8086", "db").with_auth("user", "pass");
        let actual = format!("{client:#?}");
        let expected = indoc! { r#"
            Client {
                url: "https://localhost:8086",
                parameters: {
                    "db": "db",
                    "p": "<redacted>",
                    "u": "user",
                },
                ..
            }
        "# };
        assert_eq!(actual.trim(), expected.trim());
    }

    #[test]
    fn test_fn_database() {
        let client = Client::new("http://localhost:8068", "database");
        assert_eq!(client.database_name(), "database");
        assert_eq!(client.database_url(), "http://localhost:8068");
    }

    #[test]
    fn test_with_auth() {
        let client = Client::new("http://localhost:8068", "database");
        assert_eq!(client.parameters.len(), 1);
        assert_eq!(client.parameters.get("db").unwrap(), "database");

        let with_auth = client.with_auth("username", "password");
        assert_eq!(with_auth.parameters.len(), 3);
        assert_eq!(with_auth.parameters.get("db").unwrap(), "database");
        assert_eq!(with_auth.parameters.get("u").unwrap(), "username");
        assert_eq!(with_auth.parameters.get("p").unwrap(), "password");

        let client = Client::new("http://localhost:8068", "database");
        let with_auth = client.with_token("token");
        assert_eq!(with_auth.parameters.len(), 1);
        assert_eq!(with_auth.parameters.get("db").unwrap(), "database");
        assert_eq!(with_auth.token.unwrap(), "token");
    }
}

//! Errors that might happen in the crate

use thiserror::Error;

#[derive(Debug, Eq, PartialEq, Error)]
#[non_exhaustive]
pub enum Error {
    #[error("query is invalid: {error}")]
    /// Error happens when a query is invalid
    InvalidQueryError { error: String },

    #[error("Failed to build URL: {error}")]
    /// Error happens when a query is invalid
    UrlConstructionError { error: String },

    #[error("http protocol error: {error}")]
    /// Error happens when a query is invalid
    ProtocolError { error: String },

    #[error("http protocol error: {error}")]
    /// Error happens when Serde cannot deserialize the response
    DeserializationError { error: String },

    #[error("InfluxDB encountered the following error: {error}")]
    /// Error which has happened inside InfluxDB
    DatabaseError { error: String },

    #[error("API error with a status code: {0}")]
    /// Error happens when API returns non 2xx status code.
    ApiError(u16),

    #[error("connection error: {error}")]
    /// Error happens when HTTP request fails
    ConnectionError { error: String },
}

use super::{Series, TaggedSeries};
use serde::de::{
    value, Deserialize, DeserializeSeed, Deserializer, Error, IntoDeserializer, MapAccess,
    SeqAccess, Visitor,
};
use serde_derive::Deserialize;
use std::fmt;
use std::marker::PhantomData;

// Based on https://serde.rs/deserialize-struct.html
impl<'de, T> Deserialize<'de> for Series<T>
where
    T: Deserialize<'de>,
{
    fn deserialize<D>(deserializer: D) -> Result<Self, D::Error>
    where
        D: Deserializer<'de>,
    {
        // Field name deserializer
        #[derive(Deserialize)]
        #[serde(field_identifier, rename_all = "lowercase")]
        enum Field {
            Name,
            Columns,
            Values,
        }

        struct SeriesVisitor<T> {
            _inner_type: PhantomData<T>,
        }

        impl<'de, T> Visitor<'de> for SeriesVisitor<T>
        where
            T: Deserialize<'de>,
        {
            type Value = Series<T>;

            fn expecting(&self, formatter: &mut fmt::Formatter) -> fmt::Result {
                formatter.write_str("struct Series")
            }

            fn visit_map<V>(self, mut map: V) -> Result<Series<T>, V::Error>
            where
                V: MapAccess<'de>,
            {
                let mut name = None;
                let mut columns: Option<Vec<String>> = None;
                let mut values: Option<Vec<T>> = None;
                while let Some(key) = map.next_key()? {
                    match key {
                        Field::Name => {
                            if name.is_some() {
                                return Err(Error::duplicate_field("name"));
                            }
                            name = Some(map.next_value()?);
                        }
                        Field::Columns => {
                            if columns.is_some() {
                                return Err(Error::duplicate_field("columns"));
                            }
                            columns = Some(map.next_value()?);
                        }
                        Field::Values => {
                            if values.is_some() {
                                return Err(Error::duplicate_field("values"));
                            }
                            // Error out if "values" is encountered before "columns"
                            // Hopefully, InfluxDB never does this.
                            if columns.is_none() {
                                return Err(Error::custom(
                                    "series values encountered before columns",
                                ));
                            }
                            // Deserialize using a HeaderVec deserializer
                            // seeded with the headers from the "columns" field
                            values = Some(map.next_value_seed(HeaderVec::<T> {
                                header: columns.as_ref().unwrap(),
                                _inner_type: PhantomData,
                            })?);
                        }
                    }
                }
                let name = name.ok_or_else(|| Error::missing_field("name"))?;
                let values = values.unwrap_or_default();

                Ok(Series { name, values })
            }
        }

        const FIELDS: &[&str] = &["name", "values"];
        deserializer.deserialize_struct(
            "Series",
            FIELDS,
            SeriesVisitor::<T> {
                _inner_type: PhantomData,
            },
        )
    }
}

// Based on https://serde.rs/deserialize-struct.html
impl<'de, TAG, T> Deserialize<'de> for TaggedSeries<TAG, T>
where
    TAG: Deserialize<'de>,
    T: Deserialize<'de>,
{
    fn deserialize<D>(deserializer: D) -> Result<Self, D::Error>
    where
        D: Deserializer<'de>,
    {
        // Field name deserializer
        #[derive(Deserialize)]
        #[serde(field_identifier, rename_all = "lowercase")]
        enum Field {
            Name,
            Tags,
            Columns,
            Values,
        }

        struct SeriesVisitor<TAG, T> {
            _tag_type: PhantomData<TAG>,
            _value_type: PhantomData<T>,
        }

        impl<'de, TAG, T> Visitor<'de> for SeriesVisitor<TAG, T>
        where
            TAG: Deserialize<'de>,
            T: Deserialize<'de>,
        {
            type Value = TaggedSeries<TAG, T>;

            fn expecting(&self, formatter: &mut fmt::Formatter) -> fmt::Result {
                formatter.write_str("struct TaggedSeries")
            }

            fn visit_map<V>(self, mut map: V) -> Result<TaggedSeries<TAG, T>, V::Error>
            where
                V: MapAccess<'de>,
            {
                let mut name = None;
                let mut tags: Option<TAG> = None;
                let mut columns: Option<Vec<String>> = None;
                let mut values: Option<Vec<T>> = None;
                while let Some(key) = map.next_key()? {
                    match key {
                        Field::Name => {
                            if name.is_some() {
                                return Err(Error::duplicate_field("name"));
                            }
                            name = Some(map.next_value()?);
                        }
                        Field::Tags => {
                            if tags.is_some() {
                                return Err(Error::duplicate_field("tags"));
                            }
                            tags = Some(map.next_value()?);
                        }
                        Field::Columns => {
                            if columns.is_some() {
                                return Err(Error::duplicate_field("columns"));
                            }
                            columns = Some(map.next_value()?);
                        }
                        Field::Values => {
                            if values.is_some() {
                                return Err(Error::duplicate_field("values"));
                            }
                            // Error out if "values" is encountered before "columns"
                            // Hopefully, InfluxDB never does this.
                            if columns.is_none() {
                                return Err(Error::custom(
                                    "series values encountered before columns",
                                ));
                            }
                            // Deserialize using a HeaderVec deserializer
                            // seeded with the headers from the "columns" field
                            values = Some(map.next_value_seed(HeaderVec::<T> {
                                header: columns.as_ref().unwrap(),
                                _inner_type: PhantomData,
                            })?);
                        }
                    }
                }
                let name = name.ok_or_else(|| Error::missing_field("name"))?;
                let tags = tags.ok_or_else(|| Error::missing_field("tags"))?;
                let values = values.ok_or_else(|| Error::missing_field("values"))?;
                Ok(TaggedSeries { name, tags, values })
            }
        }

        const FIELDS: &[&str] = &["name", "tags", "values"];
        deserializer.deserialize_struct(
            "TaggedSeries",
            FIELDS,
            SeriesVisitor::<TAG, T> {
                _tag_type: PhantomData,
                _value_type: PhantomData,
            },
        )
    }
}

// Deserializer that takes a header as a seed
// and deserializes an array of arrays into a
// Vec of map-like values using the header as
// keys and the values as values.
struct HeaderVec<'h, T> {
    header: &'h [String],
    _inner_type: PhantomData<T>,
}

impl<'de, 'h, T> DeserializeSeed<'de> for HeaderVec<'h, T>
where
    T: Deserialize<'de>,
{
    type Value = Vec<T>;

    fn deserialize<D>(self, deserializer: D) -> Result<Self::Value, D::Error>
    where
        D: Deserializer<'de>,
    {
        struct HeaderVecVisitor<'h, T> {
            header: &'h [String],
            _inner_type: PhantomData<T>,
        }
        impl<'de, 'h, T> Visitor<'de> for HeaderVecVisitor<'h, T>
        where
            T: Deserialize<'de>,
        {
            type Value = Vec<T>;

            fn expecting(&self, formatter: &mut fmt::Formatter) -> fmt::Result {
                write!(formatter, "an array of arrays")
            }

            fn visit_seq<A>(self, mut seq: A) -> Result<Vec<T>, A::Error>
            where
                A: SeqAccess<'de>,
            {
                let mut vec = Vec::new();

                while let Some(v) = seq.next_element_seed(RowWithHeader {
                    header: self.header,
                    _inner_type: PhantomData,
                })? {
                    vec.push(v);
                }

                Ok(vec)
            }
        }
        deserializer.deserialize_seq(HeaderVecVisitor {
            header: self.header,
            _inner_type: PhantomData,
        })
    }
}

// Deserializer that takes a header as a seed
// and deserializes an array into a map-like
// value using the header as keys and the values
// as values.
struct RowWithHeader<'h, T> {
    header: &'h [String],
    _inner_type: PhantomData<T>,
}

impl<'de, 'h, T> DeserializeSeed<'de> for RowWithHeader<'h, T>
where
    T: Deserialize<'de>,
{
    type Value = T;

    fn deserialize<D>(self, deserializer: D) -> Result<Self::Value, D::Error>
    where
        D: Deserializer<'de>,
    {
        struct RowWithHeaderVisitor<'h, T> {
            header: &'h [String],
            _inner: PhantomData<fn() -> T>,
        }

        impl<'de, 'h, T> Visitor<'de> for RowWithHeaderVisitor<'h, T>
        where
            T: Deserialize<'de>,
        {
            type Value = T;

            fn expecting(&self, formatter: &mut fmt::Formatter) -> fmt::Result {
                formatter.write_str("array")
            }

            fn visit_seq<A>(self, seq: A) -> Result<T, A::Error>
            where
                A: SeqAccess<'de>,
            {
                // `MapAccessDeserializer` is a wrapper that turns a `MapAccess`
                // into a `Deserializer`, allowing it to be used as the input to T's
                // `Deserialize` implementation. T then deserializes itself using
                // the entries from the map visitor.
                Deserialize::deserialize(value::MapAccessDeserializer::new(HeaderMapAccess {
                    header: self.header,
                    field: 0,
                    data: seq,
                }))
            }
        }

        deserializer.deserialize_seq(RowWithHeaderVisitor {
            header: self.header,
            _inner: PhantomData,
        })
    }
}

// MapAccess implementation that holds a reference to
// the header for keys and a serde sequence for values.
// When asked for a key, it returns the next header and
// advances its header field index. When asked for a value,
// it tries to deserialize the next element in the serde
// sequence into the desired type, and returns an error
// if no element is returned (the sequence is exhausted).
struct HeaderMapAccess<'h, A> {
    header: &'h [String],
    field: usize,
    data: A,
}

impl<'de, 'h, A> MapAccess<'de> for HeaderMapAccess<'h, A>
where
    A: SeqAccess<'de>,
{
    type Error = <A as SeqAccess<'de>>::Error;

    fn next_key_seed<K: DeserializeSeed<'de>>(
        &mut self,
        seed: K,
    ) -> Result<Option<K::Value>, Self::Error> {
        let field = match self.header.get(self.field) {
            None => return Ok(None),
            Some(field) => field,
        };
        self.field += 1;
        seed.deserialize(field.clone().into_deserializer())
            .map(Some)
    }

    fn next_value_seed<K: DeserializeSeed<'de>>(
        &mut self,
        seed: K,
    ) -> Result<K::Value, Self::Error> {
        match self.data.next_element_seed(seed)? {
            Some(value) => Ok(value),
            None => Err(Error::custom("next_value_seed called but no value")),
        }
    }
}

#[cfg(test)]
mod tests {
    use super::Series;
    use std::borrow::Cow;
    use std::collections::HashMap;

    const TEST_DATA: &str = r#"
    {
        "name": "series_name",
        "columns": ["foo", "bar"],
        "values": [
            ["foo_a", "bar_a"],
            ["foo_b", "bar_b"]
        ]
    }
    "#;

    // we can derive all the impls we want here
    #[derive(Debug, PartialEq, Eq)]
    struct EqSeries<T> {
        pub name: String,
        pub values: Vec<T>,
    }

    impl<T> From<Series<T>> for EqSeries<T> {
        fn from(Series { name, values }: Series<T>) -> Self {
            EqSeries { name, values }
        }
    }

    #[test]
    fn test_deserialize_cow() {
        // Unfortunately, Cow is not automatically borrowed,
        // so this is basically equivalent to String, String
        let result = serde_json::from_str::<Series<HashMap<Cow<str>, Cow<str>>>>(TEST_DATA);
        assert!(result.is_ok());
        assert_eq!(
            EqSeries::from(result.unwrap()),
            EqSeries {
                name: "series_name".into(),
                values: vec![
                    {
                        let mut h = std::collections::HashMap::new();
                        h.insert("foo".into(), "foo_a".into());
                        h.insert("bar".into(), "bar_a".into());
                        h
                    },
                    {
                        let mut h = std::collections::HashMap::new();
                        h.insert("foo".into(), "foo_b".into());
                        h.insert("bar".into(), "bar_b".into());
                        h
                    },
                ],
            },
        );
    }

    #[test]
    fn test_deserialize_borrowed() {
        use serde_derive::Deserialize;

        // Deserializing a string that cannot be passed through
        // without escaping will result in an error like this:
        // `invalid type: string "\n", expected a borrowed string at line 6 column 43`
        // but if it doesn't need escaping it's fine.
        #[derive(Deserialize, Debug, PartialEq, Eq)]
        struct BorrowingStruct<'a> {
            foo: &'a str,
            bar: &'a str,
        }

        let result = serde_json::from_str::<Series<BorrowingStruct>>(TEST_DATA);
        assert!(result.is_ok(), "{}", result.unwrap_err());
        assert_eq!(
            EqSeries::from(result.unwrap()),
            EqSeries {
                name: "series_name".into(),
                values: vec![
                    BorrowingStruct {
                        foo: "foo_a",
                        bar: "bar_a",
                    },
                    BorrowingStruct {
                        foo: "foo_b",
                        bar: "bar_b",
                    },
                ],
            },
        );
    }
}

//! Serde Integration for InfluxDB. Provides deserialization of query returns.
//!
//! When querying multiple series in the same query (e.g. with a regex query), it might be desirable to flat map
//! the resulting series into a single `Vec` like so. The example assumes, that there are weather readings in multiple
//! series named `weather_<city_name>` (e.g. `weather_berlin`, or `weather_london`). Since we're using a Regex query,
//! we don't actually know which series will be returned. To assign the city name to the series, we can use the series
//! `name`, InfluxDB provides alongside query results.
//!
//! ```rust,no_run
//! use influxdb::{Client, Query};
//! use serde_derive::Deserialize;
//!
//! #[derive(Deserialize)]
//! struct WeatherWithoutCityName {
//!     temperature: i32,
//! }
//!
//! #[derive(Deserialize)]
//! struct Weather {
//!     city_name: String,
//!     weather: WeatherWithoutCityName,
//! }
//!
//! # #[tokio::main]
//! # async fn main() -> Result<(), influxdb::Error> {
//! let client = Client::new("http://localhost:8086", "test");
//! let query = Query::raw_read_query(
//!     "SELECT temperature FROM /weather_[a-z]*$/ WHERE time > now() - 1m ORDER BY DESC",
//! );
//! let mut db_result = client.json_query(query).await?;
//! let _result = db_result
//!     .deserialize_next::<WeatherWithoutCityName>()?
//!     .series
//!     .into_iter()
//!     .map(|mut city_series| {
//!         let city_name =
//!             city_series.name.split("_").collect::<Vec<&str>>().remove(2);
//!         Weather {
//!             weather: city_series.values.remove(0),
//!             city_name: city_name.to_string(),
//!         }
//!     })
//!     .collect::<Vec<Weather>>();
//! # Ok(())
//! # }
//! ```

mod de;

use serde::de::DeserializeOwned;
use serde_derive::Deserialize;

use crate::{client::check_status, Client, Error, Query, ReadQuery};

#[derive(Deserialize)]
#[doc(hidden)]
struct _DatabaseError {
    error: String,
}

#[derive(Deserialize, Debug)]
#[doc(hidden)]
pub struct DatabaseQueryResult {
    pub results: Vec<serde_json::Value>,
}

impl DatabaseQueryResult {
    pub fn deserialize_next<T: 'static>(&mut self) -> Result<Return<T>, Error>
    where
        T: DeserializeOwned + Send,
    {
        serde_json::from_value::<Return<T>>(self.results.remove(0)).map_err(|err| {
            Error::DeserializationError {
                error: format!("could not deserialize: {err}"),
            }
        })
    }

    pub fn deserialize_next_tagged<TAG, T: 'static>(
        &mut self,
    ) -> Result<TaggedReturn<TAG, T>, Error>
    where
        TAG: DeserializeOwned + Send,
        T: DeserializeOwned + Send,
    {
        serde_json::from_value::<TaggedReturn<TAG, T>>(self.results.remove(0)).map_err(|err| {
            Error::DeserializationError {
                error: format!("could not deserialize: {err}"),
            }
        })
    }
}

#[derive(Deserialize, Debug)]
#[doc(hidden)]
pub struct Return<T> {
    #[serde(default = "Vec::new")]
    pub series: Vec<Series<T>>,
}

#[derive(Debug)]
/// Represents a returned series from InfluxDB
pub struct Series<T> {
    pub name: String,
    pub values: Vec<T>,
}

#[derive(Deserialize, Debug)]
#[doc(hidden)]
pub struct TaggedReturn<TAG, T> {
    #[serde(default = "Vec::new")]
    pub series: Vec<TaggedSeries<TAG, T>>,
}

#[derive(Debug)]
/// Represents a returned series from InfluxDB
pub struct TaggedSeries<TAG, T> {
    pub name: String,
    pub tags: TAG,
    pub values: Vec<T>,
}

impl Client {
    pub async fn json_query(&self, q: ReadQuery) -> Result<DatabaseQueryResult, Error> {
        let query = q.build().map_err(|err| Error::InvalidQueryError {
            error: err.to_string(),
        })?;

        let read_query = query.get();
        let read_query_lower = read_query.to_lowercase();

        if !read_query_lower.contains("select") && !read_query_lower.contains("show") {
            let error = Error::InvalidQueryError {
                error: "Only SELECT and SHOW queries supported with JSON deserialization".into(),
            };
            return Err(error);
        }

        let url = &format!("{}/query", &self.url);
        let mut parameters = self.parameters.as_ref().clone();
        parameters.insert("q", read_query);
        let mut request_builder = self.client.get(url);
        if let Some(ref token) = self.token {
            request_builder = request_builder.header("Authorization", format!("Token {token}"))
        }
        let request_builder = request_builder.query(&parameters);

        let res = request_builder
            .send()
            .await
            .map_err(|err| Error::ConnectionError {
                error: err.to_string(),
            })?;
        check_status(&res)?;

        let body = res.bytes();

        let body = body.await.map_err(|err| Error::ProtocolError {
            error: err.to_string(),
        })?;

        // Try parsing InfluxDBs { "error": "error message here" }
        if let Ok(error) = serde_json::from_slice::<_DatabaseError>(&body) {
            return Err(Error::DatabaseError { error: error.error });
        }

        // Json has another structure, let's try actually parsing it to the type we're deserializing
        serde_json::from_slice::<DatabaseQueryResult>(&body).map_err(|err| {
            Error::DeserializationError {
                error: format!("serde error: {err}"),
            }
        })
    }
}

//! Pull requests are always welcome. See [Contributing](https://github.com/influxdb-rs/influxdb-rust/blob/main/CONTRIBUTING.md) and [Code of Conduct](https://github.com/influxdb-rs/influxdb-rust/blob/main/CODE_OF_CONDUCT.md). For a list of past changes, see [CHANGELOG.md](https://github.com/influxdb-rs/influxdb-rust/blob/main/CHANGELOG.md).
//!
//! ## Currently Supported Features
//!
//! -   Reading and writing to InfluxDB
//! -   Optional Serde support for deserialization
//! -   Running multiple queries in one request (e.g. `SELECT * FROM weather_berlin; SELECT * FROM weather_london`)
//! -   Writing single or multiple measurements in one request (e.g. `WriteQuery` or `Vec<WriteQuery>` argument)
//! -   Authenticated and unauthenticated connections
//! -   `async`/`await` support
//! -   `#[derive(InfluxDbWriteable)]` derive macro for writing / reading into structs
//! -   `GROUP BY` support
//! -   Tokio and async-std support (see example below) or [available backends](https://github.com/influxdb-rs/influxdb-rust/blob/main/influxdb/Cargo.toml)
//! -   Swappable HTTP backends ([see below](#Choice-of-HTTP-backend))
//!
//! # Quickstart
//!
//! Add the following to your `Cargo.toml`
//!
#![doc = cargo_toml!(indent="", "derive")]
//!
//! For an example with using Serde deserialization, please refer to [serde_integration](crate::integrations::serde_integration)
//!
//! ```rust,no_run
//! use chrono::{DateTime, Utc};
//! use influxdb::{Client, Error, InfluxDbWriteable, ReadQuery, Timestamp};
//!
//! #[tokio::main]
//! async fn main() -> Result<(), Error> {
//!     // Connect to db `test` on `http://localhost:8086`
//!     let client = Client::new("http://localhost:8086", "test");
//!
//!     #[derive(InfluxDbWriteable)]
//!     struct WeatherReading {
//!         time: DateTime<Utc>,
//!         humidity: i32,
//!         #[influxdb(tag)]
//!         wind_direction: String,
//!     }
//!
//!     // Let's write some data into a measurement called `weather`
//!     let weather_readings = vec![
//!         WeatherReading {
//!             time: Timestamp::Hours(1).into(),
//!             humidity: 30,
//!             wind_direction: String::from("north"),
//!         }
//!         .into_query("weather"),
//!         WeatherReading {
//!             time: Timestamp::Hours(2).into(),
//!             humidity: 40,
//!             wind_direction: String::from("west"),
//!         }
//!         .into_query("weather"),
//!     ];
//!
//!     client.query(weather_readings).await?;
//!
//!     // Read back all records
//!     let read_query = ReadQuery::new("SELECT * FROM weather");
//!
//!     let read_result = client.query(read_query).await?;
//!     println!("{}", read_result);
//!     Ok(())
//! }
//! ```
//!
//! For further examples, check out the integration tests in `tests/integration_tests.rs`
//! in the repository.
//!
//! # Choice of HTTP backend
//!
//! To communicate with InfluxDB, you can choose the HTTP backend to be used configuring the appropriate feature. We recommend sticking with the default reqwest-based client, unless you really need async-std compatibility.
//!
//! - **[hyper](https://github.com/hyperium/hyper)** (through reqwest, used by default), with [rustls](https://github.com/ctz/rustls)
#![doc = cargo_toml!(indent="\t", "derive")]
//! - **[hyper](https://github.com/hyperium/hyper)** (through reqwest), with native TLS (OpenSSL)
#![doc = cargo_toml!(indent="\t", default-features = false, "derive", "serde", "reqwest-client-native-tls")]
//! - **[hyper](https://github.com/hyperium/hyper)** (through reqwest), with vendored native TLS (OpenSSL)
#![doc = cargo_toml!(indent="\t", default-features = false, "derive", "serde", "reqwest-client-native-tls-vendored")]
